import React, { Component } from "react";
import Axios from "axios";
import Header from '../../Components/Header';
import GetAuthorisation from "../../Funciton/GetAuthorisation"
import DatePicker from "react-datepicker";
import 'react-confirm-alert/src/react-confirm-alert.css'; 
import { confirmAlert } from 'react-confirm-alert';
import PriceModal from "../../Components/Modal/PriceModal";
import "react-datepicker/dist/react-datepicker.css";
import Services from "../../Components/Modal/Services";

const API_URL = "http://localhost:8080";

class BoatsPage extends Component {



	state = {
		boats: [],
		services: [],
		selectedBoat:"",
		selectedDate: new Date(),
		hours: new Date().getHours(),
		minutes: new Date().getMinutes(),
		days:0,
		location:"",
		grade: 1,
		
		maxCount: "",
		price: 0,
	
		hiddenSuccessfulAlert: true,
		successfulHeader: "",
		successfulMessage: "",
		hiddenUnsuccessfulAlert: true,
		unsuccessfulHeader: "",
		unsuccessfulMessage: "",
		
		openPriceModal:false,
		openReserveModal:false,
		loggedClient: false
	};

    handleFormToogle = () => {
		this.setState({ formShowed: !this.state.formShowed });
	};

	componentDidMount() {
		this.state.loggedClient= this.hasRole("ROLE_CLIENT");
		console.log(localStorage.getItem("keyRole"));
		Axios.get(API_URL + "/boat/allBoats")

			.then((res) => {
				console.log(res.data);
				this.setState({ boats: res.data });
			})
			.catch((err) => {
				console.log(err);
			});

	}

	hasRole = (requestRole) => {
		let currentRoles = JSON.parse(localStorage.getItem("keyRole"));
	
		if (currentRoles === null) return false;
	
	
		for (let currentRole of currentRoles) {
		  if (currentRole === requestRole) return true;
		}
		return false;
	  };



	handleResetSearch = () => {
		Axios.get(API_URL, {

			validateStatus: () => true,
			headers: { Authorization: GetAuthorisation() },
		})
			.then((res) => {
				this.setState({
					drugs: res.data,
					formShowed: false,
					showingSearched: false,
				
					searchGrade: "",
				
					
				});
			})
			.catch((err) => {
				console.log(err);
			});


	};







	handleModalClose = () => {
		this.setState({ openPriceModal: false });
	};

    handleShowPrice = (services) => {
		this.setState({ openPriceModal: true ,services:services});
        console.log("eeeeeeeeeeeeeeeeeeeee");
        console.log(this.state.openPriceModal);
	};


	handleDateChange = (date) => {
		this.setState({ selectedDate: date });
	};

	handleMinutesChange = (event) => {
		if (event.target.value < 0) this.setState({ minutes: 0 });
        else if (event.target.value > 59) this.setState({ minutes: 59 });
		else this.setState({ minutes: event.target.value });
	};

	handleHoursChange = (event) => {
        if (event.target.value < 0) this.setState({ hours: 0 });
		else if (event.target.value > 23) this.setState({ hours: 23 });
		else this.setState({ hours: event.target.value });
	};

	handleDaysChange = (event) => {
     this.setState({ days: event.target.value });
	};

	handleLocationChange = (event) => {
		this.setState({ location: event.target.value });
	};

	handleGradeChange = (event) => {
		this.setState({ grade: event.target.value });
	};

	handleOnSelect = (boatId,services)=>{
		this.setState({selectedBoat:boatId,openReserveModal: true,services:services});

	};

	reserveAppointmentForPharmacist = (pharmacist) => {
		
		this.setState({
			
		
			hiddenUnsuccessfulAlert: true,
			UnsuccessfulHeader: "",
			UnsuccessfulMessage: "",
			errorMessageForReservation: ""
		});

		console.log(this.state.selectedDate);


		let reservationDTO = {
			pharmacistId: pharmacist.Id,
			startDate: this.state.consultationDate,
		};

		Axios.post(
			API_URL + "/appointment/reserveConsulationBySelectedPharmacist", reservationDTO ,
			{ validateStatus: () => true, headers: { Authorization: GetAuthorisation() } }
		)
			.then((res) => {
				if (res.status === 401) {
					this.props.history.push("/login");
			
				} else if (res.status === 500) {
					this.setState({ errorMessageForReservation : res.data,
						 showReservedConsultationModal: true,
						  modalTitle : "Error" });
				 } else if (res.status === 400) {
					this.setState({ errorMessageForReservation : res.data, 
						showReservedConsultationModal: true,
						modalTitle : "Bad request"});
				 } else if (res.status === 201 ){
						console.log("uspesno zakao termin");
					
						this.setState({ showReservedConsultationModal: true,
									 errorMessageForReservation : "",
									 hideSuccessfulModalText : false,
									 hideSuccessfulModalButton: false,
									 modalTitle : "Successful reservation"
							  });
				  }	
		
			})
			.catch((err) => {
				console.log(err);
				this.setState({ errorMessageForReservation : "Some error happened", modalTitle : "Error" });
			});





	};


	handleSearchClick = () => {
		{
			let gradeFrom = this.state.searchGradeFrom;
			let gradeTo = this.state.searchGradeTo;
			let name = this.state.searchName;
			let searchMan = this.state.searchMan;

			if (gradeFrom === "") gradeFrom = -1;
			if (gradeTo === "") gradeTo = -1;
			if (name === "") name = "";
			if (searchMan === "") searchMan = "";

			Axios.get("http://localhost:8080/drug/searchDrugsInPharmacy", {
				params: {
					name: name,
					gradeFrom: gradeFrom,
					gradeTo: gradeTo,
					manufacturer: searchMan,
					pharmacyId: localStorage.getItem("keyPharmacyId")
				},
				validateStatus: () => true,
				headers: { Authorization: GetAuthorisation() },
			})
				.then((res) => {
					console.log(res.data);
					this.setState({
						drugs: res.data,
						formShowed: false,
						showingSearched: true,
					});
				})
				.catch((err) => {
					console.log(err);
				});
		}
	};


	render() {


		return (

			<React.Fragment>

				<Header />

				<div id="allBoats" >

					<div className="container" style={{ marginTop: "2em" }} >
						<h2 className=" text-center  mt-2 text-uppercase">Brodovi</h2>
					
					</div>


					<div style={{ width: "70%", marginTop: "3em", marginLeft: "auto", marginRight: "auto" }} width="100%">


						<button className="btn btn-outline-primary btn-xl" type="button" onClick={this.handleFormToogle}>

							Pretraga
						</button>

						
						<form className={this.state.formShowed ? "form-inline mt-3" : "form-inline mt-3 collapse"} width="100%" id="formCollapse">
						<div className="form-row justify-content-center">
                            <div className="mr-2">
								<div style={{ fontSize: "20px" }}>
                                    Izaberi datum:
								</div>

								<DatePicker
									className="form-control"
									minDate={new Date()}
									onChange={(date) => this.handleDateChange(date)}
									selected={this.state.selectedDate}
								/>
						    </div>
							<div className="mr-2">
								<div style={{ fontSize: "20px" }}>
									Sati:
								</div>
								<input
                                    type="number"
									min="00"
									max="23"
                                    className="form-control"
									onChange={this.handleHoursChange}
									value={this.state.hours}
									style={{ width: "9em" }}
									
								/>
							</div>
							<div className="mr-2">
								<div style={{ fontSize: "20px" }}>
                                    Minuti:
								</div>
								<input
									min="00"
                                    max="59"
                                    type="number"
									className="form-control"
									onChange={this.handleMinutesChange}
									value={this.state.minutes}
									style={{ width: "9em" }}
									
									
								/>
							</div>

							<div className="mr-2">
								<div style={{ fontSize: "20px" }}>
                                    Broj dana:
								</div>
								<input
									min="00"
                                    max="59"
                                    type="number"
									className="form-control"
									onChange={this.handleDaysChange}
									value={this.state.days}
									style={{ width: "9em" }}
									
									
								/>
							</div>



							<div className="mr-2">
								<div style={{ fontSize: "20px" }}>
                                    Lokacija:
								</div>
								<input
                                    type="text"
									className="form-control "
									onChange={this.handleLocationChange}
									value={this.state.location}
									style={{ width: "9em" }}
									
									
								/>
							</div>

							<div className="mr-2">
								<div style={{ fontSize: "20px" }}>
                                    Ocena:
								</div>
								<input
                                    min="0"
									max="5"
                                    type="number"
									className="form-control"
									onChange={this.handleGradeChange}
									value={this.state.grade}
									style={{ width: "9em" }}
									
									
								/>
							</div>
							<div className="mr-2" >	
							
								<button
									style={{ background: "#1977cc" , marginTop:"30px"}}
									onClick={this.handleSearchClick}
									className="btn btn-primary btn-x2"
									type="button"
								>
									<i className="icofont-search mr-1"></i>
									Pretraži
								</button>
							</div>	
							</div>
						</form>

						<div className={this.state.showingSearched ? "form-group mt-2" : "form-group mt-2 collapse"}>
							<button type="button" className="btn btn-outline-secondary" onClick={this.handleResetSearch}>
								<i className="icofont-close-line mr-1"></i>Reset
							</button>
						</div>


						<table className={this.state.loggedClient === true ? "table table-hover" : "table"} style={{ width: "85%", marginTop: "3rem" }}>
							<tbody>
								{this.state.boats.map((boat) => (
									<tr id={boat.Id} key={boat.Id} >
										<td width="300em" >
											<div style={{ marginTop: "5%" }}>
												<img className="img-fluid" src={`data:image/jpeg;base64,${boat.EntityDTO.image}`} width="300em"  />
											</div>
										</td>
										<td width="50%" onClick={() => this.handleOnSelect(boat.Id,boat.EntityDTO.services)}> 
											<div style={{ marginTop: "2%" }}>
												<b>Naziv:</b> {boat.EntityDTO.name}
											</div>
											<div>
												<b>Adresa:</b> {boat.EntityDTO.address}
											</div>
											<div>
												<b>Ocena:</b> {boat.EntityDTO.avgGrade}
												<i className="icon-star" style={{ color: "yellow" }}></i>
											</div>
                                            <div>
												<b>Kratak opis:</b> {boat.EntityDTO.description}
											</div>
										</td>
                                        <td className="align-middle">
											<div>
												<button
													type="button"
													onClick={() => this.handleShowPrice(boat.EntityDTO.services)}

													className="btn btn-outline-primary btn-block"
												>
													Cenovnik
												</button>
											
											</div>
                                        </td>
									</tr>
								))}
							</tbody>
						</table>





					</div>

					<Services show={this.state.openReserveModal} onCloseModal={this.handleModalClose} prices={this.state.services}  header="Cenovnik" />				
                    <PriceModal show={this.state.openPriceModal} onCloseModal={this.handleModalClose} prices={this.state.services}  header="Cenovnik" />
				</div>

             
			</React.Fragment>


		);
	}
}

export default BoatsPage;
