import React, { Component } from 'react';
import { Button, Modal } from 'react-bootstrap';
import Axios from 'axios';

import DatePicker from "react-datepicker";

import GetAuthorisation from "../../Funciton/GetAuthorisation";
import { Redirect } from "react-router-dom";
const API_URL="http://localhost:8080";
class Services extends Component {
    state = {
        servicesToAdd: [] ,
        servicesS:[],
        page: 1,
        modalSize:'lg',    
        selectedDate:new Date(),
        pharmacyId:'',
        drugForAdd:'',
        drugRemove:'',
        selectedCount:'',
        showDate:false,
  
    }

 

    componentDidMount() {
		this.setState({servicesS:this.props.prices})

    }

    addItem = item => {
        this.setState({
            servicesToAdd: [
            ...this.state.servicesToAdd,
            item 
          ]
        })
    }

    removeItem(e) {
        var array = [...this.state.servicesS];
        var index = array.indexOf(e)
        if (index !== -1) {
          array.splice(index, 1);
          this.setState({servicesS: array});
        }
      }
  
      addItem1 = item => {
        this.setState({
            servicesS: [
            ...this.state.servicesS,
            item 
          ]
        })
    }

    removeItem1(e) {
        var array = [...this.state.servicesToAdd];
        var index = array.indexOf(e)
        if (index !== -1) {
          array.splice(index, 1);
          this.setState({servicesToAdd: array});
        }
      }
  

    handleAdd = (serviceAdd) => {

        let tagDTO = {
            Id: serviceAdd.Id,
            EntityDTO:{
           
            name: serviceAdd.EntityDTO.name,
            price:serviceAdd.EntityDTO.price
            }
        }
        console.log(this.props.boat.EntityDTO)
        console.log(this.props.prices)
        console.log(this.props.boat.EntityDTO.name)
        console.log(this.props.boat.EntityDTO.image)
        console.log("Bidibou");
        console.log(tagDTO);
        this.removeItem(this.props.prices);
        console.log('res.data');
        console.log(this.state.drugForAdd);
        this.addItem(tagDTO);
        
       
    
            this.setState({
                page:1,
                selectedCount:'',
                drugForAdd:''
            })   
        
    }

    handleRemove = (serviceRemove) => {
           
            console.log(serviceRemove)
    
            let tagDTO = {
                Id: serviceRemove.Id,
                EntityDTO:{
               
                name: serviceRemove.EntityDTO.name,
                price:serviceRemove.EntityDTO.price
                }
            }

            console.log("Bidibou");
            console.log(tagDTO);
         
            this.removeItem1(serviceRemove);
            console.log('res.data');
            console.log(this.state.drugForAdd);
            this.addItem1(tagDTO);
    
            this.setState({
                page:1,
                selectedCount:'',
            })   
        }
    

    handleCreateOrder = () =>{
        let createOrderDTO = {
            pharmacyId:this.props.pharmacyId,
            listOfDrugs: this.state.drugsToAdd,
            dateTo:this.state.selectedDate
        }
      console.log(createOrderDTO)
        Axios
        .post(API_URL + "/order/addNewOrder", createOrderDTO, {
            validateStatus: () => true,
            headers: { Authorization: GetAuthorisation() },})
        .then((res) =>{
            console.log(res.status)
            if (res.status === 201) {
                alert("New Oreder is succesfully created");
                this.handleClickOnClose();
            }else {
                alert("It is not possibe to create new order");
            }
        }).catch((err) => {
        });
    }

    isValidData = (addDrugToPharmacyDTO) =>{
        if(addDrugToPharmacyDTO.drugId===''){
            return false
        }

        if(addDrugToPharmacyDTO.dateTo===new Date()){
            return false
        }

        if(addDrugToPharmacyDTO.amount<1){
            return false
        }

        if(addDrugToPharmacyDTO.price<1){
            return false
        }

        return true
    }

    handleSelectedDateChange = (date) => {
        this.setState({
            selectedDate:date,
        });

    }


    handleSelectedCountChange = (event) => {
        this.setState({selectedCount:event.target.value});
    }

    handleClickOnCreateOrder = () =>{
        if(this.state.drugsToAdd.length<1){
          alert("Drug list is empty");       
        }else{
            this.setState({
                page:3,
            })
        }
    }

    handleClickOnClose = () => {
        this.setState({
            drugsToAdd: [] ,
            drugs:[],
            page: 1,    
            selectedDate:new Date(),
            pharmacyId:'',
            drugForAdd:'',
            selectedCount:'',
        });
        this.handleUpdte();
        this.props.closeModal();
    }

    handleUpdte = () => {
        Axios.get(API_URL + "/drug/drugsInPharmacy/" + localStorage.getItem("keyPharmacyId"), {
			headers: { Authorization: GetAuthorisation() },
		})

			.then((res) => {
				console.log(res.data);
				this.setState({ drugs: res.data });
			})
			.catch((err) => {
				console.log(err);
			});

    }

    onAddClick = (id) =>{
        this.setState({page : 2,drugForAdd:id});
  

    }

 

     handleBack = (event) =>{
        this.setState({page: 1,selectedCount:''});
    

    }



    render() { 
      
       
        return ( 
            <Modal
                show = {this.props.show}
                size='lg'
                centered
                >
                <Modal.Header >
                    <Modal.Title style={{marginLeft:'37%'}} >
                        Create Order
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <p className="mt-2 text-uppercase" style={{ fontSize: "1.5em" }}>
								Datum rezervacije:{" "}
								{new Date(this.props.startDate).toLocaleDateString("en-US", {
									day: "2-digit",
									month: "2-digit",
									year: "numeric",
									hour: "2-digit",
									minute: "2-digit",
								})}
                                {" "}
                                {new Date(this.props.endDate).toLocaleDateString("en-US", {
									day: "2-digit",
									month: "2-digit",
									year: "numeric",
									hour: "2-digit",
									minute: "2-digit",
								})}
							</p>
                <table className= "table" style={{ width: "100%", marginTop: "3rem" }} hidden={!this.props.show}>
							<tbody>
									<tr id={this.props.boat.Id} key={this.props.boat.Id} >
										<td width="300em" >
											<div style={{ marginTop: "5%" }}>
												<img className="img-fluid" src={`data:image/jpeg;base64,${this.props.image}`} width="300em"  />
											</div>
										</td>
										<td width="50%" > 
											<div style={{ marginTop: "2%" }}>
												<b>Naziv:</b> {this.props.name}
											</div>
											<div>
												<b>Adresa:</b> {this.props.address}
											</div>
											<div>
												<b>Ocena:</b> {this.props.grade}
												<i className="icon-star" style={{ color: "yellow" }}></i>
											</div>
                                            <div>
												<b>Kratak opis:</b> {this.props.description}
											</div>
											<div>
												<b>Cena:</b> {this.props.price}
											</div>
										</td>
									</tr>
								
							</tbody>
						</table>
                        <div className="checkList">
    <div className="title">Your CheckList:</div>
    <div className="list-container">
      {checkList.map((item, index) => (
         <div key={index}>
           <span>{item}</span>
         </div>
      ))}
    </div>
  </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button  hidden={this.state.page===3} onClick={() => this.handleClickOnCreateOrder()}>Create order</Button>
                    <Button onClick={() => this.handleClickOnClose()}>Close</Button>
                </Modal.Footer>
            </Modal>
         );
    }
}
 
export default Services;
